function [out1,out2,out3,out4]=myhpbutter(freq,order)% [sys]=myhpbutter(freq,order)%   returns the transfer function for a highpass %   Butterworth filter with unity gain, knee at 'freq', %   and 'order' poles.%   Brian Lantz% if called with 2 arguments, returns the numerator and denominator of a TF%   as [num,den]=myhpbutter(freq,order)% updated to return system on Jan 16 2001 BTL[butZ, butP, K]=butter(order,2*pi*freq,'high','s');%butPscaled= 2*pi * freq*butP;%gain=(2*pi*freq)^order;switch nargout    case 1	out1=zpk(butZ,butP, K);    case 2	[out1,out2]=zp2tf(butZ,butP, K);    case 4        out1 = zpk(butZ,butP, K);        out2 = transpose(butZ) / (-2*pi); % [Hz]        out3 = transpose(butP) / (-2*pi); % [Hz]        out4 = K;    otherwise        disp('output can either be')        disp('nargout == 1: out = cont. zpk object of filter')        disp('nargout == 2: out1 = NUM, out2 = DEN (i.e. output of zp2tf)')        disp('nargout == 4: out1 = cont. zpk object of filter, out2 = zeros [Hz], out3 = poles [Hz], out4 = gain')end